const Reducer = (state, action) => {
    switch (action.type) {
        case "LOGIN_START":
            return {
                user: null,
                isFetching: true,
                error: false
            };
        case "LOGIN_SUCCESS":
            return {
                user: action.payload,
                isFetching: false,
                error: false,
            };
        case "LOGIN_FAILURE":
            return {
                user: null,
                isFetching: false,
                error: true,
            };

        case "LOGOUT":
            return {//null,false,false like the initial position again
                user: null,
                isFetching: true,
                error: false,
            };
        default:
            return state;
    }
}

export default Reducer
